jQuery(document).off("karma_finish_form_builder.uploadImages").on("karma_finish_form_builder.uploadImages",function(e,viewObject){if(null==document.querySelector(".karma-upload-image-container")){return}function callback(frame){var input=document.querySelector(".karma-upload-image-input"),content=document.querySelector(".karma-upload-image-content"),attachment=frame.state().get("selection").first().toJSON();input.value=attachment.url;content.style.backgroundImage="url( "+input.value+" )";content.classList.add("karma-upload-image-has-image");jQuery(input).trigger("input")}function openMediaLibrary(){var addImgLink=document.querySelector(".karma-upload-image-button");window.top.karmaBuilderEnviroment.openMediaLibrary(addImgLink,callback)}openMediaLibrary();function removeImage(){document.querySelector(".karma-cancel-image-button").addEventListener("click",function(){var input=document.querySelector(".karma-upload-image-input"),content=document.querySelector(".karma-upload-image-content");input.value="none";content.style.backgroundImage="none";jQuery(input).trigger("input")})}removeImage()});